# обработчик команды '/start'
@bot.message_handler(commands=['start'])
def start_command_handler(message: types.Message):
    # отправляем ответ на команду '/start'
    bot.send_message(
        chat_id=message.chat.id, # id чата, в который необходимо направить сообщениего на русский) \nВведи интересующий термин, например, логи', # текст сообщения  
        text='Приветствую! Я, Сергей Иванович Ожегов! В нынешнее время совсем позабыли правила русского языка и образовалось много новых слов, особенно в сфере IT. Я время зря не терял и создал новый словарик и поэтому постараюсь помочь тебе расшифровать сложные термины и аббревиатуры. Если незнакомое слово будет тебе не понятно, то попробуй указать его на английском языке. Введи интересующий термин, например, "логи". Также я знаю статус коды, а ещё некоторые команды linux-консоль. Но если все же не нашлось нужного определения, не серчай я со временем доработаю!', # текст сообщения
    )

# обработчик всех остальных сообщений
@bot.message_handler()
def message_handler(message: types.Message):
    # пробуем найти ключевую фразу в словаре
    definition = DEFINITOINS.get(
        message.text.lower(), # приводим текст сообщения к нижнему регистру
    )
    # если фразы нет в словаре, то переменная definition будет иметь значение None
    # проверяем это условие
    if definition is None:
        # если ключевая фраза не была найдена в словаре
        # отправляем ответ
        bot.send_message(
            chat_id=message.chat.id,
            text='Я пока не слыхал о таком, попробуй поискать на просторах интернета',
        )
        # выходим из функции
        return
    
    # если ключевая фраза была найдена, формируем текст сообщения и отправляем его
    # если перед строкой поставить букву f, то в фигурных скобках {} можно использовать переменные :)
    bot.send_message(
        chat_id=message.chat.id,
        text=f'Определение:\n<code>{definition}</code>',
    )

    bot.send_message(
        chat_id=message.chat.id,
        text=f'Валяй следующий термин',
    )


# главная функция программы
def main():
    # запускаем нашего бота
    bot.infinity_polling()


if __name__ == '__main__':
    main()